   var xmlDoc ;
  //var Element = $(xmlDoc).find('EBS');  
  xmlDoc ="<EBS><DeviceName>/dev/sda2</DeviceName>";
   xmlDoc +='<VolumeSize>'+$(DeviceSize).val()+'</VolumeSize>';
   xmlDoc +='<EbsType>'+$(EbsType).val()+'</EbsType>';

  if  ($(IOPS).val()!='' || $(EbsType).val()=='iops') 
  {
    xmlDoc +='<IOPS>'+$(IOPS).val()+'</IOPS></EBS>';
  }
  else
  {
       xmlDoc +='<IOPS/></EBS>'
  }

 // var EBSvalue = XMLToString(xmlDoc)
 // hdnEBS.val(EBSvalue)
  IsEBSRequested.val('<IsEBSRequested>Y</IsEBSRequested>');
  hdnEBS.val(xmlDoc);
 // alert(xmlDoc)
