function customcontrolinsertvalues(ctrl)
{ 
  debugger;
  var currentControl = $(ctrl);
  var allControlDiv = currentControl.closest('.AllControl')

  var currentInstance = allControlDiv.attr('class').match(/item_\d+/)[0];   

  var currentCustomControlContainer = currentControl.closest('.customcontrolcontainer');

  var currentControlCategory = $(currentCustomControlContainer).children().first().attr("controlcategory");

  if(currentControlCategory == 'Disk')
  {
    var DName = ["xvdaa", "xvdab", "xvdac"];
    var IOPSControl = currentCustomControlContainer.find("[tag='IOPS']");
    var EbsTypeControl = currentCustomControlContainer.find("[tag='EbsType']");

    if ($(EbsTypeControl[0]).val() == 'io1' && $(IOPSControl[0]).val().trim() == '')
      {
        jAlert('Please enter IOPS Value for Ebs Type "io1"','MyCloud')
        return false;
      }

    var hdnDiskCount = $(allControlDiv.find("[name=hdnDiskCount]")).val();      
    var itemsAdded = parseInt(hdnDiskCount) + 1;               
    var DeviceNameControl = currentCustomControlContainer.find("[tag='DeviceName']");    
    var machineName = $('.tab-content .' + currentInstance).find("[tag=VMName]");

    if(machineName && machineName.length>0)
    {
      machineName = $(machineName[0]).val();
    }

    //DeviceNameControl.val(machineName + '-' + itemsAdded);
    DeviceNameControl.val(DName[hdnDiskCount]);
    $(allControlDiv.find("[name=hdnDiskCount]")).val(itemsAdded);
  }
  else
  {
    //debugger;
    var customcontrolcount = $(ctrl).closest('.customcontrolcontainer').siblings('.div_customcontrol_add').length
	var ctrlsnoid = $(ctrl).closest('.customcontrolcontainer').find('input[tag=SNo]');
	var ctrldeviceindexid = $(ctrl).closest('.customcontrolcontainer').find('input[tag=DeviceIndex]');
    $(ctrlsnoid).val(customcontrolcount+1);
    $(ctrldeviceindexid).val(customcontrolcount);

    var LoctionName=$('.tab-content .' + currentInstance).find("[name='ddlAvailabilityZone']")[0].value
    var customControlNetSubnet=  $(ctrl).closest('.customcontrolcontainer').find('select[tag=Subnet]');
    var MySubnetData=GetAWSSubNet(LoctionName,'','',false)
    var VPCs = JSON.parse(MySubnetData).filter(function (entry) {
    return entry.text===customControlNetSubnet[0].value;
	});
    $('.tab-content .' + currentInstance).find("[name='hdnvpc']")[0].value=VPCs[0].VPCID;
    GetAWSSubNet(LoctionName,VPCs[0].VPCID,customControlNetSubnet[0],true)
  }
   return true; 
}

